package com.syntax.class02;

public class classProgram1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double num1 = 5.15;
		double num2 = -5.515;
		double mult = num1 * num1;
		double dif = num1 - num1;
		double div = num1 / num1;
		double sum = num1 + num1;
		System.out.println("The result of " + num1 + " + " + num2 + " = " + sum);
		System.out.println("The result of " + num1 + " * " + num2 + " = " + mult);
		System.out.println("The result of " + num1 + " / " + num2 + " = " + div);
		System.out.println("The result of " + num1 + " - " + num2 + " = " + dif);
				
		
		
		
		
		
		
	}

}
